import "./styles.css";
import { useCallback, useEffect, useState } from "react";
import { getAsset } from "./service/assetService";
import { Asset } from "./types/types";
import "./App.css";
import {
  Button,
  Layout,
  Image,
  Descriptions,
  Typography,
  Col,
  Row
} from "antd"

const { Header, Content } = Layout;
const { Paragraph } = Typography;

const eyeColors: Array<string> = [
  "Blue",
  "Green",
  "Brown",
  "Hazel",
  "Black",
  "Amber",
  "Yellow",
];

export default function App() {
  const [asset, setAsset] = useState<Asset[]>([])
  const [error, setError] = useState(false)

  const randomEyeColor: string = eyeColors[Math.floor(Math.random() * eyeColors.length)];

  const getNextAsset = useCallback(() => {
    getAsset()
    .then((data: Asset[]) => setAsset(data))
    .catch((error: string) => setError(true));
  }, [])

  useEffect(() => {
    getNextAsset();
  }, [getNextAsset])

  return (
    <>
      <Typography>
        <Header>
          <Row>
            <Col span={22}>
              Asset Detail Page
            </Col>
            <Col span={2}>
              <Button type="primary" onClick={getNextAsset}>
                Next Asset
              </Button>
            </Col>
          </Row>
        </Header>
        <Content>
          {error !== true ? (
            asset.map((value) => {
              return (
          <Paragraph code key={value.login.uuid}>
            <Row>
              <Col>
                <Image 
                  width={386}
                  height={386}
                  src={value.picture.large}
                  fallback="data:image/png;base64,"
                />
              </Col>
              <Col>
                <Descriptions title={"Asset Codename: " +  value.login.uuid + " A.K.A " + value.login.username }>
                  <Descriptions.Item label="First Name">
                  {value.name.first}
                  </Descriptions.Item>
                  <Descriptions.Item label="Last Name">
                  {value.name.last}
                  </Descriptions.Item>
                  <Descriptions.Item label="Codename (A.K.A.)">
                  {value.login.username}
                  </Descriptions.Item>
                  <Descriptions.Item label="City">
                  {value.location.city}
                  </Descriptions.Item>
                  <Descriptions.Item label="State">
                  {value.location.state}
                  </Descriptions.Item>
                  <Descriptions.Item label="Country">
                  {value.location.country}
                  </Descriptions.Item>
                  <Descriptions.Item label="Gender">
                  {value.gender}
                  </Descriptions.Item>
                  <Descriptions.Item label="DOB">
                  {value.dob.date} (Age: {value.dob.age})
                  </Descriptions.Item>
                  <Descriptions.Item label="Eye Color">
                  {randomEyeColor}
                  </Descriptions.Item>
                  <Descriptions.Item label="TimeZone Offset">
                  {value.location.timezone.offset}
                  </Descriptions.Item>
                  <Descriptions.Item label="TimeZone Description">
                  {value.location.timezone.description}
                  </Descriptions.Item>
                </Descriptions>
              </Col>
            </Row>
          </Paragraph>
              )
            })
          ) : (
            <Content>
            An Error Occurred. Please Try Again:
            <Button type="primary" onClick={getNextAsset}>
              Retrieve Asset
            </Button>
          </Content>
          )}
        </Content>
      </Typography>

      
    </>
  );
}
